import React, { useEffect, useState } from "react";
import Container from "../../Shared/Container";
import OurServicecard from "../Reusablecomponents/Pricing/OurServicecard";
import useAxiosCommon from "../../hooks/api-hooks/useAxiosCommon";
import toast from "react-hot-toast";
import LoadingSpinner from "../LoadingSpinner";
import { motion } from 'framer-motion';
const OurService = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const axiosCommon = useAxiosCommon();
  const svgComponents = [
    Servicesvg0,
    Servicesvg1,
    Servicesvg2,
    Servicesvg3,
    Servicesvg4,
    Servicesvg5,
  ];

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await axiosCommon.get("/service");
        setServices(response.data.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching services:", error);
        toast.error("Failed to load services");
        setLoading(false);
      }
    };

    fetchServices();
  }, []);

  return (
    <section className="py-10 md:py-20 xl:py-[130px]">
      <Container className="">
        <h3 className="text-[28px] md:text-[34px] lg:text-[40px] font-semibold text-center text-[#007570] uppercase">
          Why Us?
        </h3>

        {loading ? (
          <LoadingSpinner loading={loading}/>
        ) : (
          <motion.div  
          
          
          initial={{ opacity: 0, y: 20,  filter: 'blur(10px)' }}
          whileInView={{ opacity: 1, y: 0, filter: 'blur(0px)' }}
          transition={{ duration: 1, ease: 'easeInOut' }}
          viewport={{ once: true, amount: 0.3 }}
          
          
          className="grid lg:grid-cols-3 gap-3 md:gap-5 pt-4 md:pt-8 justify-center w-fit mx-auto">
            {services.map((service, index) => {
              const SvgComponent = svgComponents[index]
                ? svgComponents[index]
                : svgComponents[0];
              return (
                <OurServicecard
                  key={index}
                  svg={<SvgComponent />}
                  title={service.title}
                  description={service.description}
                />
              );
            })}
          </motion.div>
        )}
      </Container>
    </section>
  );
};

export default OurService;

